(37, -1, 'skimlinks_global_active_succ', 'cpglobal', '<strong>Account Activation Successful</strong><br /><br />\r\n					Congratulations, we''ve activated your Skimlinks account and your Publisher ID has been associated with this plugin.<br /><br />\r\n					<strong>Please click Finish to complete the Activation process.</strong><br />&nbsp;<br />\r\n					You will shortly receive a welcome email to the address that you supplied containing your Skimlinks account and login details. If you don''t receive this email in the\r\n					next few minutes, please check your junk email folder. If it still isn''t there, please contact support@skimlinks.com.<br /><br />', 'skimlinks', 'Skimlinks', 1297120017, '3.0'),
(38, -1, 'skimlinks_global_alert_js', 'cpglobal', 'To finalize your account dissociation, please press OK and then the Save button on the Global Options page of the Skimlinks plugin.\\n\\nOnce this is done, your Skimlinks account association will be removed from this plugin.\\n\\nSkimlinks products will no longer be active on your site.\\n\\nTo associate a different Skimlinks account, re-associate the same account, or activate a new account with this plugin, please use the appropriate buttons in the Setup section.', 'skimlinks', 'Skimlinks', 1297120027, '3.0'),
