(596, -1, 'bbcode_add_edit_strip_empty_text', 'cphelptext', 'Setting this option will remove empty tags (eg. [tag][/tag]).', 'vbulletin', 'vBulletin Solutions', 1191575557, '3.7.0 Beta 1'),
(597, -1, 'bbcode_add_edit_strip_empty_title', 'cphelptext', 'Remove Tag if Empty', 'vbulletin', 'vBulletin Solutions', 1194351163, '3.7.0 Beta 1'),
(598, -1, 'bbcode_add_edit_stop_parse_text', 'cphelptext', 'Setting this option will make any BB code tags within this BB code tag display as plain text.<br /><br />\r\n\r\nFor example\r\n<blockquote>[tag][b]some text here[/b][/tag]</blockquote>\r\n\r\nWill show as\r\n\r\n<blockquote>[b]some text here[/b]</blockquote>\r\n\r\nIf set, and\r\n\r\n<blockquote><b>some text here</b></blockquote>\r\n\r\nIf not set', 'vbulletin', 'vBulletin Solutions', 1206965837, '3.7.0 Release Candidate 2'),
(599, -1, 'bbcode_add_edit_stop_parse_title', 'cphelptext', 'Disable BB Code Use Within This BB Code', 'vbulletin', 'vBulletin Solutions', 1206965892, '3.7.0 Release Candidate 2'),
(600, -1, 'bbcode_add_edit_disable_smilies_text', 'cphelptext', 'Setting this option will make any smilies within this tag show as plain text.', 'vbulletin', 'vBulletin Solutions', 1191575937, '3.7.0 Beta 1'),
