(2387, -1, 'profilefield_add_edit_perline_title', 'cphelptext', 'Items Per Line (<i>Radio, Checkbox only</i>)', 'vbulletin', 'vBulletin Solutions', 1147805177, '3.6.0 Beta 1'),
(2388, -1, 'profilefield_add_edit_boxheight_text', 'cphelptext', 'This setting is how tall the box should be. If you have 12 options and want 3 of them displayed without the user having to use the scrollbar, set this to 3. It is recommended that you set this to at least 3 for user sanity reasons.', 'vbulletin', 'vBulletin Solutions', 0, ''),
(2389, -1, 'profilefield_add_edit_boxheight_title', 'cphelptext', 'Box Height (<i>Select Multiple only</i>)', 'vbulletin', 'vBulletin Solutions', 0, ''),
(2390, -1, 'profilefield_add_edit_displayorder_text', 'cphelptext', 'This sets the order in which fields are displayed. The default is to add it to the end of your current fields. Display order starts at 1 and can be any whole positive number (e.g. don''t use decimals or fractions).', 'vbulletin', 'vBulletin Solutions', 0, ''),
