(2394, -1, 'profilefield_add_edit_editable_text', 'cphelptext', '<b>Yes</b> - User may edit this field at any time.<br />\r\n<br />\r\n<b>No</b>  - User may never edit this field.<br />\r\n<br />\r\n<b>Only at registration</b> - User may only edit this field at registration or if it is set to be required and it is blank.', 'vbulletin', 'vBulletin Solutions', 1147803250, '3.6.0 Beta 1'),
(2395, -1, 'profilefield_add_edit_editable_title', 'cphelptext', 'Field Editable by User?', 'vbulletin', 'vBulletin Solutions', 0, ''),
(2396, -1, 'profilefield_add_edit_hidden_text', 'cphelptext', 'If this is set to Yes, this field can be filled in by the user but will only be viewable to those with the proper usergroup permission.', 'vbulletin', 'vBulletin Solutions', 0, ''),
(2397, -1, 'profilefield_add_edit_hidden_title', 'cphelptext', 'Private Field', 'vbulletin', 'vBulletin Solutions', 0, ''),
(2398, -1, 'profilefield_add_edit_searchable_text', 'cphelptext', 'If this is set to No, users will not be able to search using this field on the Member''s List Advanced Search. If this field is set to Private than only those users with the proper usergroup permission will be able to search on it.', 'vbulletin', 'vBulletin Solutions', 0, ''),
