(2530, -1, 'rssposter_edit_stickthread_text', 'cphelptext', 'Select this option if you would like threads posted from this feed to be made into sticky threads, which remain at the top of the forumdisplay list.', 'vbulletin', 'vBulletin Solutions', 1149683771, '3.6.0 Beta 1'),
(2531, -1, 'rssposter_edit_stickthread_title', 'cphelptext', 'Make Thread Sticky', 'vbulletin', 'vBulletin Solutions', 1149683771, '3.6.0 Beta 1'),
(2532, -1, 'rssposter_edit_moderatethread_text', 'cphelptext', 'If you would like your moderation staff to be able to review each item posted from this RSS feed before it appears on your forums, set this option to ''Yes''.', 'vbulletin', 'vBulletin Solutions', 1149683930, '3.6.0 Beta 1'),
(2533, -1, 'rssposter_edit_moderatethread_title', 'cphelptext', 'Moderate Thread', 'vbulletin', 'vBulletin Solutions', 1149683930, '3.6.0 Beta 1'),
(2534, -1, 'rssposter_edit_threadactiondelay_text', 'cphelptext', 'If items are posted as threads, you may specify a delay in hours here and have the system then perform an action on the thread.<br />\r\n<br />\r\nFor example, you could specify a delay of 6 hours, then have the system close threads posted by this RSS feed after that delay.<br />\r\n<br />\r\nA setting of ''0'' disables the thread action delay, so no actions will be automatically applied to threads after they are posted.', 'vbulletin', 'vBulletin Solutions', 1149684592, '3.6.0 Beta 1'),
(2535, -1, 'rssposter_edit_threadactiondelay_title', 'cphelptext', 'Thread Action Delay', 'vbulletin', 'vBulletin Solutions', 1149684477, '3.6.0 Beta 1'),
(2536, -1, 'rssposter_edit_unstickthread_text', 'cphelptext', 'Select this option to have threads posted by this RSS feed unstuck after the delay specified by ''Thread Action Delay''.', 'vbulletin', 'vBulletin Solutions', 1149684720, '3.6.0 Beta 1'),
