(2540, -1, 'rssposter_edit_allowhtml_text', 'cphelptext', 'If you enable this option, any HTML in RSS feed items will be allowed <em>as HTML</em> in announcements posted by the feed.<br />\r\n<br />\r\n<strong>Do not enable this option unless you trust the RSS feed source completely, as your board could be hacked if the RSS feed source contains malicious HTML.</strong>', 'vbulletin', 'vBulletin Solutions', 1149684884, '3.6.0 Beta 1'),
(2541, -1, 'rssposter_edit_allowhtml_title', 'cphelptext', 'Allow HTML in Announcements', 'vbulletin', 'vBulletin Solutions', 1149684884, '3.6.0 Beta 1'),
