(6230, -1, 'activateaccount', 'emailbody', 'Dear $username,\r\n\r\nThank you for registering at the $vboptions[bbtitle]. Before we can activate your account one last step must be taken to complete your registration.\r\n\r\nPlease note - you must complete this last step to become a registered member. You will only need to visit this URL once to activate your account.\r\n\r\nTo complete your registration, please visit this URL:\r\n$vboptions[bburl]/register.php?a=act&u=$userid&i=$activateid\r\n\r\n**** Does The Above URL Not Work? ****\r\nIf the above URL does not work, please use your Web browser to go to:\r\n$vboptions[bburl]/register.php?a=ver\r\n\r\nPlease be sure not to add extra spaces. You will need to type in your username and activation number on the page that appears when you visit the URL.\r\n\r\nYour Username is: $username\r\nYour Activation ID is: $activateid\r\n\r\nIf you are still having problems signing up please contact a member of our support staff at $vboptions[webmasteremail]\r\n\r\nAll the best,\r\n$vboptions[bbtitle]\r\n\r\n\r\n--------------------\r\nTo stop receiving this email, please visit this URL:\r\n$vboptions[bburl]/register.php?do=deleteactivation&u=$userid&i=$activateid ', 'vbulletin', 'vBulletin Solutions', 1314722734, '4.1.7 Beta 1');
INSERT INTO `phrase` (`phraseid`, `languageid`, `varname`, `fieldname`, `text`, `product`, `username`, `dateline`, `version`) VALUES
(6231, -1, 'activateaccount_change', 'emailbody', 'Dear $username,\r\n\r\nSince you have recently changed your email, we require that you verify your new email address. You will only need to visit the URL once to have your account updated.\r\n\r\nTo complete reverification, please visit this URL:\r\n$vboptions[bburl]/register.php?a=act&u=$userid&i=$activateid\r\n\r\n**** Does The Above URL Not Work? ****\r\nIf the above URL does not work, please use your Web browser to go to:\r\n$vboptions[bburl]/register.php?a=ver\r\n\r\nPlease to be sure not to add extra spaces. You will need to type in your username and activation number on the page that appears when you visit the url.\r\n\r\nYour Username is: $username\r\nYour Activation ID is: $activateid\r\n\r\nIf you are still having problems verifying please contact a member of our support staff at $vboptions[webmasteremail]\r\n\r\nAll the best,\r\n$vboptions[bbtitle]\r\n\r\n\r\n--------------------\r\nTo stop receiving this email, please visit this URL:\r\n$vboptions[bburl]/register.php?do=deleteactivation&u=$userid&i=$activateid', 'vbulletin', 'vBulletin Solutions', 1315606795, '4.1.8 Beta 1'),
(6232, -1, 'attachfull', 'emailbody', '$bbuserinfo[username] has just tried to attach a file that would exceed the $vboptions[attachtotalspace] byte quota you setup by $overage.\r\n\r\nIn order to allow users to continue attaching files, we recommend you delete some attachments. You can perform attachment management on the following pages:\r\n\r\n$vboptions[bburl]/$admincpdir/attachment.php?do=intro\r\n$vboptions[bburl]/$admincpdir/attachment.php?do=stats', 'vbulletin', '', 0, ''),
(6233, -1, 'birthday', 'emailbody', 'Hello $username,\r\n\r\nWe at $vboptions[bbtitle] would like to wish you a happy birthday today!', 'vbulletin', '', 0, ''),
(6234, -1, 'contactus', 'emailbody', 'The following message was sent to you via the $vboptions[bbtitle] Contact Us form by $name ( mailto:$email ).\r\n\r\n--------------------------------\r\n\r\n$message\r\n\r\n--------------------------------\r\n\r\nReferring Page: $url\r\nIP Address: $ip\r\nUser Name: $bbuserinfo[username]\r\nUser ID: $bbuserinfo[userid]\r\nEmail: $email', 'vbulletin', 'vBulletin Solutions', 1197640651, '3.7.0 Beta 2'),
(6235, -1, 'digestforum', 'emailbody', 'Dear $forum[username],\r\n\r\nYou are subscribed to the forum $forum[title_clean], there have been $newthreads new thread(s) and $updatedthreads updated thread(s).\r\n$forumlink\r\n\r\nThe following threads are new:\r\n$newthreadbits\r\n\r\nThe following threads have been updated:\r\n$updatedthreadbits\r\n\r\nAll the best,\r\n$vboptions[bbtitle]\r\n\r\n~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~\r\nUnsubscription information:\r\n\r\nTo unsubscribe from this forum, please visit this page:\r\n$unsubscribelink\r\n', 'vbulletin', 'vBulletin Solutions', 1284394480, '4.1.1 Alpha 1'),
(6236, -1, 'digestgroup', 'emailbody', 'Dear $group[username],\r\n\r\nYou are subscribed to the group $group[name], there have been $new_discussions new discussion(s) and $updated_discussions updated discussions(s).\r\n$grouplink\r\n\r\nThe following discussions are new:\r\n$new_discussion_bits\r\n\r\nThe following discussions have been updated:\r\n$updated_discussion_bits\r\n\r\nAll the best,\r\n$vboptions[bbtitle]', 'vbulletin', 'vBulletin Solutions', 1283976780, '4.1.0'),
(6237, -1, 'digestgroupbit', 'emailbody', '************\r\n$discussion[title]\r\n$discussionlink\r\nIn group: $group[name]\r\nStarted by: $discussion[postusername]\r\nLast post: $discussion[lastreplydate] $discussion[lastreplytime]', 'vbulletin', 'vBulletin Solutions', 1283976836, '4.1.0'),
(6238, -1, 'digestpostbit', 'emailbody', '************\r\n$post[title]\r\n$postlink\r\nPosted by: $post[postusername]\r\nOn: $post[postdate] $post[posttime]\r\n\r\n$post[pagetext]\r\n', 'vbulletin', 'vBulletin Solutions', 1284406247, '4.1.0'),
(6239, -1, 'digestthread', 'emailbody', 'Dear $thread[username],\r\n\r\nYou are subscribed to the thread "$thread[prefix_plain]$thread[title]" by $thread[postusername], there have been $thread[newposts] post(s) to this thread, the last poster was $thread[lastposter].\r\n$threadlink\r\n\r\nThese following posts were made to the thread:\r\n$postbits\r\n\r\nAll the best,\r\n$vboptions[bbtitle]\r\n\r\n~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~\r\nUnsubscription information:\r\n\r\nTo unsubscribe from this thread, please visit this page:\r\n$unsubscribelink\r\n', 'vbulletin', 'vBulletin Solutions', 1284394214, '4.1.0'),
(6240, -1, 'digestthreadbit', 'emailbody', '************\r\n$thread[prefix_plain]$thread[title]\r\n$threadlink\r\nIn forum: $thread[forumtitle]\r\nStarted by: $thread[postusername]\r\nLast post: $thread[lastreplydate] $thread[lastreplytime]\r\n', 'vbulletin', 'vBulletin Solutions', 1284406288, '4.1.0'),
(6241, -1, 'friendship_request_email', 'emailbody', 'Dear $userinfo[username],\r\n\r\n$fromuserinfo[username] has requested that you become friends. You can approve or deny this request by visiting:\r\n\r\n$vboptions[bburl]/profile.php?do=buddylist\r\n\r\nAll the best,\r\n$vboptions[bbtitle]', 'vbulletin', 'vBulletin Solutions', 1209745496, '3.7.1'),
(6242, -1, 'infraction_post', 'emailbody', 'This infraction has been reversed.\r\n\r\nReason: [quote]$infractioninfo[reason][/quote]', 'vbulletin', 'vBulletin Solutions', 1149280670, '3.6.0 Beta 1'),
(6243, -1, 'infraction_received_post', 'emailbody', 'Dear $infraction[username],\r\n\r\nYou have received an infraction at $vboptions[bbtitle].\r\n\r\nReason: $infraction[reason]\r\n-------\r\n$infraction[message]\r\n-------\r\n\r\nThis infraction is worth $infraction[points] point(s) and may result in restricted access until it expires.  Serious infractions will never expire.\r\n\r\nOriginal Post:\r\n$infraction[post]\r\n[quote]$infraction[pagetext][/quote]\r\n\r\nAll the best,\r\n$vboptions[bbtitle]', 'vbulletin', 'vBulletin Solutions', 1166747689, '3.6.5'),
(6244, -1, 'infraction_received_profile', 'emailbody', 'Dear $infraction[username],\r\n\r\nYou have received an infraction at $vboptions[bbtitle].\r\n\r\nReason: $infraction[reason]\r\n-------\r\n$infraction[message]\r\n-------\r\n\r\nThis infraction is worth $infraction[points] point(s) and may result in restricted access until it expires.  Serious infractions will never expire.\r\n\r\nAll the best,\r\n$vboptions[bbtitle]', 'vbulletin', 'vBulletin Solutions', 1166744440, '3.6.5'),
(6245, -1, 'infraction_thread_post', 'emailbody', 'Post: [url=$infractioninfo[postlink]]$infractioninfo[prefix_plain]$infractioninfo[threadtitle][/url]\r\nUser: [url=$infractioninfo[userlink]]$infractioninfo[username][/url]\r\nInfraction: $infractioninfo[title]\r\nPoints: $infractioninfo[points]\r\n\r\nAdministrative Note:[quote]$infractioninfo[note][/quote]\r\n\r\n\r\nMessage to User:[quote]$infractioninfo[message][/quote]\r\n\r\n\r\nOriginal Post: [quote]$postinfo[pagetext][/quote]', 'vbulletin', 'vBulletin Solutions', 1280504985, '4.1.1 Alpha 1'),
(6246, -1, 'infraction_thread_profile', 'emailbody', 'User: [url=$infractioninfo[userlink]]$infractioninfo[username][/url]\r\nInfraction: $infractioninfo[title]\r\nPoints: $infractioninfo[points]\r\n\r\nAdministrative Note:[quote]$infractioninfo[note][/quote]\r\n\r\n\r\nMessage to User:[quote]$infractioninfo[message][/quote]', 'vbulletin', 'vBulletin Solutions', 1280504951, '4.1.1 Alpha 1'),
(6247, -1, 'lostpw', 'emailbody', 'Dear $user[username],\r\n\r\nYou have requested to reset your password on $vboptions[bbtitle] because you have forgotten your password. If you did not request this, please ignore it. It will expire and become useless in 24 hours time.\r\n\r\nTo reset your password, please visit the following page:\r\n$vboptions[bburl]/login.php?a=pwd&u=$user[userid]&i=$user[activationid]\r\n\r\nWhen you visit that page, your password will be reset, and the new password will be emailed to you.\r\n\r\nYour username is: $user[username]\r\n\r\nTo edit your profile, go to this page:\r\n$vboptions[bburl]/profile.php?do=editprofile\r\n\r\nAll the best,\r\n$vboptions[bbtitle]', 'vbulletin', '', 0, ''),
(6248, -1, 'moderation_deleted', 'emailbody', 'Dear $username,\r\n\r\nUnfortunately your registration at $vboptions[bbtitle] did not meet our membership requirements. Therefore your registration was deleted.\r\n\r\nSorry,\r\n$vboptions[bbtitle]', 'vbulletin', '', 0, ''),
(6249, -1, 'moderation_validated', 'emailbody', 'Dear $username,\r\n\r\nWe have now activated your account at the $vboptions[bbtitle]. The forums can be found here:\r\n\r\n{1}\r\n\r\nYour login details are as follows:\r\nUsername: $username\r\nPassword: as you entered it when you registered\r\n\r\nDon''t forget that your password is case sensitive.\r\n\r\nTo edit your profile, please visit this page:\r\n$vboptions[bburl]/profile.php?do=editprofile\r\n\r\nAll the best,\r\n$vboptions[bbtitle]', 'vbulletin', 'vBulletin Solutions', 1279661098, '4.1.1 Alpha 1'),
(6250, -1, 'moderator', 'emailbody', 'Hello,\r\n\r\n$bbuserinfo[username] has just posted in the $foruminfo[title_clean] forum of $vboptions[bbtitle] under the title of $threadinfo[prefix_plain]$threadinfo[title].\r\n\r\nThis thread is located at $threadlink\r\n\r\nHere is the message that has just been posted:\r\n***************\r\n$post[message]\r\n***************', 'vbulletin', 'vBulletin Solutions', 1284410821, '4.1.1 Alpha 1'),
(6251, -1, 'newevent', 'emailbody', 'Hello,\r\n\r\n$bbuserinfo[username] has just posted in the $calendarinfo[title] calendar of $vboptions[bbtitle] under the title of $title.\r\n\r\nThis event is located at $vboptions[bburl]/calendar.php?do=getinfo&e=$eventid\r\n\r\nHere is the event that has just been posted:\r\n***************\r\n$eventmessage\r\n***************', 'vbulletin', '', 0, ''),
(6252, -1, 'newuser', 'emailbody', 'There is a new user, $username at $vboptions[bbtitle]\r\n\r\nTo view their profile, go here:\r\n$memberlink\r\n\r\nEmail Address : $email\r\nBirthday : $birthday\r\nReferrer: $referrer\r\nIP Address: $ipaddress\r\n\r\n$customfields', 'vbulletin', 'vBulletin Solutions', 1284411852, '4.1.1 Alpha 1'),
(6253, -1, 'notify', 'emailbody', 'Dear $touser[username],\r\n\r\n$bbuserinfo[username] has just replied to a thread you have subscribed to entitled - $threadinfo[prefix_plain]$threadinfo[title] - in the $foruminfo[title_clean] forum of $vboptions[bbtitle].\r\n\r\nThis thread is located at:\r\n$threadlink\r\n\r\nHere is the message that has just been posted:\r\n***************\r\n$pagetext\r\n***************\r\n\r\n\r\nThere may also be other replies, but you will not receive any more notifications until you visit the forum again.\r\n\r\nAll the best,\r\n$vboptions[bbtitle]\r\n\r\n~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~\r\nUnsubscription information:\r\n\r\nTo unsubscribe from this thread, please visit this page:\r\n$unsubscribelink\r\n\r\nTo unsubscribe from ALL threads, please visit this page:\r\n$managesubscriptionslink\r\n', 'vbulletin', 'vBulletin Solutions', 1284395485, '4.1.1 Alpha 1'),
(6254, -1, 'notify_discussion', 'emailbody', 'Dear $touser[username],\r\n\r\n$bbuserinfo[username] has replied to a Group discussion you have subscribed to entitled - $discussion[title] - in the $group[name] group of $vboptions[bbtitle].\r\n\r\nThis discussion is located at:\r\n$discussionlink\r\n\r\nHere is the message that has just been posted:\r\n***************\r\n$pagetext\r\n***************\r\n\r\n\r\nThere may also be other replies, but you will not receive any more notifications until you visit the forum again.\r\n\r\nAll the best,\r\n$vboptions[bbtitle]\r\n\r\n~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~\r\nUnsubscription information:\r\n\r\nTo unsubscribe from this discussion, please visit this page:\r\n$unsubscribelink\r\n\r\nTo unsubscribe from ALL discussions, please visit this page:\r\n$managegroupsubscriptionslink\r\n\r\nTo unsubscribe from ALL forum threads, please visit this page:\r\n$managesubscriptionslink', 'vbulletin', 'vBulletin Solutions', 1284399696, '4.1.0'),
(6255, -1, 'paidsubscription_reminder', 'emailbody', 'Dear $username,\r\n\r\nYour access to the paid subscription "$subscription_title" is about to expire.\r\n\r\nIf you have selected a recurring subscription you will be automatically billed for the renewal, else to renew this subscription you must visit $vboptions[bburl]/payments.php\r\n\r\nIf you do not extend your subscription, access will be removed.\r\n\r\nAll the best,\r\n$vboptions[bbtitle]', 'vbulletin', '', 0, ''),
(6256, -1, 'parentcoppa_profile', 'emailbody', 'Dear Parent/Guardian of $username,\r\n\r\n$username has just modified their account at $vboptions[bbtitle]. This can be found at:\r\n\r\n$forumhomelink\r\n\r\nThis email has been sent to you because $username has stated that they are younger than 13 years of age and $vboptions[bbtitle] is in compliance with the Children''s Online Privacy Protection Act of 1998 (COPPA).\r\n\r\nTo find out more about COPPA, please visit this page:\r\nhttp://www.ftc.gov/opa/1999/10/childfinal.htm\r\n\r\nPlease read the $vboptions[bbtitle] Privacy Statement also:\r\n$vboptions[privacyurl]\r\n\r\n$username''s profile can be viewed at:\r\n\r\n$memberlink\r\n\r\nWe thank you for helping us in keeping the privacy of children on the internet.\r\n\r\nAll the best,\r\n$vboptions[bbtitle]', 'vbulletin', 'vBulletin Solutions', 1279748882, '4.0.5'),
(6257, -1, 'parentcoppa_register', 'emailbody', 'Dear Parent/Guardian of $username,\r\n\r\n$username has just signed up for an account at $vboptions[bbtitle]. This can be found at:\r\n\r\n$forumhomelink\r\n\r\nThis email has been sent to you because $username has stated that they are younger than 13 years of age and $vboptions[bburl] is in compliance with the Children''s Online Privacy Protection Act of 1998 (COPPA).\r\n\r\nTo find out more about COPPA, please visit this page:\r\nhttp://www.ftc.gov/opa/1999/10/childfinal.htm\r\n\r\nPlease read the $vboptions[bbtitle] Privacy Statement also:\r\n$vboptions[privacyurl]\r\n\r\nYou must print out the permission form, fill it in and mail or fax it back to the webmaster. Details about how to return the form are on the form itself.\r\n\r\nThe form can be accessed through this page:\r\n\r\n$vboptions[bburl]/profile.php?do=editprofile\r\n\r\nFrom that page, you can also view the profile and make any modification you wish. You will need these login details:\r\n\r\nUsername: $username\r\nPassword: $password\r\n(Note that the password is case sensitive)\r\n\r\nWe thank you for helping us in keeping the privacy of children on the internet.\r\n\r\nAll the best,\r\n$vboptions[bbtitle]', 'vbulletin', 'vBulletin Solutions', 1279749127, '4.0.5'),
(6258, -1, 'payment_received', 'emailbody', 'A paid subscription has just been purchased by $username at  $vboptions[bbtitle]\r\n\r\nTo view their profile, go here:\r\n\r\n$memberlink\r\n\r\nUsername : $username\r\nSubscription : $subscription\r\nAmount: $amount\r\nProcessor: $processor\r\nTransaction ID: $transactionid\r\n', 'vbulletin', 'vBulletin Solutions', 1284412542, '4.1.0'),
(6259, -1, 'payment_reversed', 'emailbody', 'A paid subscription has just been reversed by $username at  $vboptions[bbtitle]\r\n\r\nTo view their profile, go here:\r\n\r\n$memberlink\r\n\r\nUsername : $username\r\nSubscription : $subscription\r\nAmount: -$amount\r\nProcessor: $processor\r\nTransaction ID: $transactionid\r\n', 'vbulletin', 'vBulletin Solutions', 1284412584, '4.1.0'),
(6260, -1, 'pmboxalmostfull', 'emailbody', 'Dear $touserinfo[username],\r\n\r\nYour private messages box on $vboptions[bbtitle] has reached 90% of your allowed quota. To ensure that you do not reach 100% of your allowed messages, you should consider archiving or deleting some of your messages. Please visit this page to do so:\r\n$vboptions[bburl]/private.php\r\n\r\nAll the best,\r\n$vboptions[bbtitle]', 'vbulletin', 'vBulletin Solutions', 1146508707, '3.6.0 Beta 1'),
(6261, -1, 'pmboxfull', 'emailbody', 'Dear $touserinfo[username],\r\n\r\n$fromusername has just tried to send you a private message. However, your private messages box on $vboptions[bbtitle] has reached the specified quota. In order to receive further private messages, you must delete some messages. Please visit this page to do so:\r\n$vboptions[bburl]/private.php\r\n\r\nAll the best,\r\n$vboptions[bbtitle]', 'vbulletin', '', 0, ''),
(6262, -1, 'pmreceived', 'emailbody', 'DO NOT REPLY TO THIS EMAIL!\r\n***************************\r\n\r\nDear $touserinfo[username],\r\n\r\nYou have received a new private message at $vboptions[bbtitle] from $fromusername, entitled "$plaintext_title".\r\n\r\nTo read the original version, respond to, or delete this message, you must log in here:\r\n$vboptions[bburl]/private.php\r\n\r\nThis is the message that was sent:\r\n***************\r\n$plaintext_message\r\n***************\r\n\r\nAgain, please do not reply to this email. You must go to the following page to reply to this private message:\r\n$vboptions[bburl]/private.php\r\n\r\nAll the best,\r\n$vboptions[bbtitle]', 'vbulletin', 'vBulletin Solutions', 1155814425, '3.6.1'),
(6263, -1, 'reminder', 'emailbody', 'Dear $username,\r\n\r\nThe following events are scheduled to begin soon at $vboptions[bbtitle].\r\n\r\nUpcoming events:\r\n$reminderbits\r\n\r\nAll the best,\r\n$vboptions[bbtitle]\r\n\r\n~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~\r\nUnsubscription information:\r\n\r\nTo unsubscribe from this email, please visit this page and delete any future reminders:\r\n$vboptions[bburl]/calendar.php?do=viewreminder', 'vbulletin', '', 0, ''),
(6264, -1, 'reminderbit', 'emailbody', '************\r\n$eventinfo[title] (In less than $hour hour(s))\r\n$vboptions[bburl]/calendar.php?do=getinfo&e=$eventinfo[eventid]\r\nIn Calendar: $eventinfo[calendar]\r\n', 'vbulletin', '', 0, ''),
(6265, -1, 'reportalbumpicture', 'emailbody', '$reportinfo[rusername] ( mailto:$reportinfo[remail] ) has reported this picture:\r\n\r\n$vboptions[bburl]/album.php?albumid=$reportinfo[albumid]&attachmentid=$reportinfo[attachmentid]\r\n\r\nThis is part of the album:\r\n''$reportinfo[albumtitle]'' by $reportinfo[username]\r\n$vboptions[bburl]/album.php?albumid=$reportinfo[albumid]\r\n\r\nThis is the reason that the user gave:\r\n$reportinfo[reason]\r\n\r\n$reportinfo[discuss]\r\n\r\nThis message has been sent to all moderators of this area, or all administrators if there are no moderators.', 'vbulletin', 'vBulletin Solutions', 1239922081, '4.0.0 Alpha 1'),
(6266, -1, 'reportalbumpicture_newthread', 'emailbody', '[url=$reportinfo[reporterlink]]$reportinfo[rusername][/url] has reported an album picture.\r\n\r\nReason:[quote]$reportinfo[reason][/quote]\r\nPicture: [url]$vboptions[bburl]/album.php?albumid=$reportinfo[albumid]&attachmentid=$reportinfo[attachmentid][/url]\r\nAlbum: [url=$vboptions[bburl]/album.php?albumid=$reportinfo[albumid]]$reportinfo[albumtitle][/url]\r\nAssigned Moderators: $reportinfo[modlist]\r\n\r\nPosted by: [url=$reportinfo[posterlink]]$reportinfo[pusername][/url]', 'vbulletin', 'vBulletin Solutions', 1280437220, '4.1.1 Alpha 1'),
(6267, -1, 'reportgroupmessage', 'emailbody', '$reportinfo[rusername] ( mailto:$reportinfo[remail] ) has reported this post:\r\n\r\n$reportinfo[messagetitle]\r\n$reportinfo[messageurl]\r\n\r\nThis is part of the Group:\r\n$reportinfo[groupname]\r\n$reportinfo[groupurl]\r\n\r\nIn the discussion:\r\n$reportinfo[discussiontitle]\r\n$reportinfo[discussionurl]\r\n\r\nThis is the reason that the user gave:\r\n$reportinfo[reason]\r\n\r\n$reportinfo[discuss]\r\n\r\nThis message has been sent to all moderators of this area, or all administrators if there are no moderators.\r\n\r\nPlease respond to this post as applicable.', 'vbulletin', 'vBulletin Solutions', 1283962671, '4.1.1 Alpha 1'),
(6268, -1, 'reportgroupmessage_newthread', 'emailbody', '[url=$reportinfo[reporterlink]]$reportinfo[rusername][/url] has reported a Group message.\r\n\r\nReason:[quote]$reportinfo[reason][/quote]\r\nMessage: [url]$reportinfo[messageurl][/url]\r\nGroup: [url]$reportinfo[groupurl][/url]\r\nDiscussion: [url]$reportinfo[discussionurl][/url]\r\n\r\nAssigned Moderators: $reportinfo[modlist]\r\n\r\nPosted by: [url=$reportinfo[posterlink]]$reportinfo[pusername][/url]\r\nOriginal Content: [quote]$reportinfo[pagetext][/quote]', 'vbulletin', 'vBulletin Solutions', 1283967782, '4.1.1 Alpha 1'),
(6269, -1, 'reportgrouppicture', 'emailbody', '$reportinfo[rusername] ( mailto:$reportinfo[remail] ) has reported this picture:\r\n\r\n$reportinfo[pictureurl]\r\n\r\nThis is part of the group:\r\n$reportinfo[groupname]\r\n$reportinfo[groupurl]\r\n\r\nThis is the reason that the user gave:\r\n$reportinfo[reason]\r\n\r\n$reportinfo[discuss]\r\n\r\nThis message has been sent to all moderators of this area, or all administrators if there are no moderators.', 'vbulletin', 'vBulletin Solutions', 1283968310, '4.1.1 Alpha 1'),
(6270, -1, 'reportgrouppicture_newthread', 'emailbody', '[url=$reportinfo[reporterlink]]$reportinfo[rusername][/url] has reported a group picture.\r\n\r\nReason:[quote]$reportinfo[reason][/quote]\r\nPicture: [url]$reportinfo[pictureurl][/url]\r\nGroup: [url]$reportinfo[groupurl][/url]\r\nAssigned Moderators: $reportinfo[modlist]\r\n\r\nPosted by: [url=$reportinfo[posterlink]]$reportinfo[pusername][/url]', 'vbulletin', 'vBulletin Solutions', 1283969627, '4.1.0'),
